<?phpnamespace MVC\Controllers;use MVC\Models\ClientModel;use MVC\Models\MailModel;use MVC\Models\ProductModel;use MVC\Models\UserModel;class IndexController extends AbstractController{    public function defaultAction(){        $this->language->load('template.common');        $this->_data['users'] = UserModel::get('select count(*) as Id from '.UserModel::getModelTableName());        $this->_data['products'] = ProductModel::get('select count(*) as Id from '.ProductModel::getModelTableName());        $this->_data['clients'] = ClientModel::get('select count(*) as Id from '.ClientModel::getModelTableName());        $mailSQL = 'SELECT count(*) as Id FROM app_mails as am ';        $mailSQL .= 'LEFT JOIN app_users as au ON am.SenderId = au.UserId ';        $mailSQL .= 'WHERE am.ReceiverId = ' . $this->session->u->UserId . ' ';        $mailSQL .= 'ORDER BY am.Created DESC';        $this->_data['mails'] = MailModel::get($mailSQL);        $this->_view();    }}